package cronjob;

public class cronjobss {

	public static void main(String[] args) {
		
		for(int i=0; i<0 ;i++)
		{
			System.out.println("Hello world");
			try {
				Thread.sleep(1000);
			} catch (InterruptedException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}
	}

}
