package day1;

public class TypeTest4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		char testMunja = '가';
		
		System.out.print(testMunja);
		System.out.print(testMunja);
		
		testMunja = 'A' ;
		System.out.print(testMunja);
		
		testMunja = '%' ;
		System.out.print(testMunja);
		
		testMunja = '\n' ;    //  \n은 대행 문자.
		System.out.print(testMunja);
		
		testMunja = '#' ;
		System.out.print(testMunja);
		
		testMunja = '\t' ;    // tab 문자  
		System.out.print(testMunja);
		
		testMunja = '9' ;
		System.out.print(testMunja);
		
	}

}

