package day3;

public class ForTest1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int su=1; su<=10; su++)
		{
			System.out.print(su);
		}
		System.out.println("");
		System.out.println("---------------------");
		
		for(int su=1; su<=10; su+=2)
		{
			System.out.print(su);
		}
		System.out.println("");
		
		System.out.println("---------------------");
		
		for(int su=10; su>=0; su--)
		{
			System.out.print(su);
		}
	}

}
